class Solution
{
	public:
		int mod = 1e9 + 7;
	int solve(int wi, int ti, vector<string> &words, string target, vector<vector< long long >> &cnt, vector< vector< long long>> &dp)
	{
		if (ti == target.length())
			return 1;
		if (wi == words[0].length())
			return 0;

		if (dp[wi][ti] != -1)
			return dp[wi][ti];

		long long ways = 0;
		ways += (solve(wi + 1, ti + 1, words, target, cnt, dp) *cnt[wi][target[ti] - 'a']) % mod;

		ways += solve(wi + 1, ti, words, target, cnt, dp) % mod;
		return dp[wi][ti] = (ways % mod);
	}

	int numWays(vector<string> &words, string target)
	{
		int W = words[0].length(), L = target.length();

		vector<vector < long long>> cnt(W + 1, vector < long long > (27, 0));
		vector<vector < long long>> dp(W + 1, vector < long long > (L + 1, 0));

		for (int i = 0; i < words.size(); i++)
		{
			for (int j = 0; j < W; j++)
			{
				cnt[j][words[i][j] - 'a'] += 1;
			}
		}

		for (int wi = W; wi >= 0; wi--)
		{
			for (int ti = L; ti >= 0; ti--)
			{
				if (ti == L)
				{
					dp[wi][ti] = 1;
					continue;
				}

				if (wi == W)
				{
					dp[wi][ti] = 0;
					continue;
				}

				long long ways = 0;
				ways += (dp[wi + 1][ti + 1] *cnt[wi][target[ti] - 'a']) % mod;

				ways += dp[wi + 1][ti] % mod;
				dp[wi][ti] = (ways % mod);
			}
		}

		return dp[0][0] % mod;
	}
};

