class Solution {

    static int mod = 1000000007;
    public void match(int[] result, String[] words, String target, StringBuilder input, int previousWordIndex) {

        if (input.length() == target.length()) {
            if(input.toString().equals(target)) {
                result[0] = (result[0]+1)%mod;
            }
            return;
        }

        for (String word : words) {
            for (int i= previousWordIndex+1; i< words[0].length(); i++) {
                input.append(word.charAt(i));
                if (target.startsWith(input.toString())) {
                    match(result, words, target, input, i);
                }
                input.deleteCharAt(input.length()-1);
            }  
        }


    }

     public int numWays(String[] words, String target) {
        int[] result = new int[1];
        match(result, words, target, new StringBuilder(), -1);
        return result[0];
    }
}
