#include <string.h>

int numWays(char ** words, int wordsSize, char * target) 
{
    int t = strlen(target);
    int w = strlen(words[0]); // Fixed width of all the words in 'words' pointer

    /* Getting memory for a frequency matrix for a specific character in the current column of the words 2 D array. 26 ==> 26 letters in alphabet */
    unsigned long long * f = malloc(sizeof(unsigned long long)  * w * 26);

    /* Initializing with Zero */
    for (int i = 0; i < 26; i++)
        for (int j = 0; j < w; j++)
            f[(i * w) + j] = 0;

    /* Transition matrix */
    unsigned long long * trans = malloc(sizeof(unsigned long long) * (t + 1) * (w + 1));
    for (int i = 0; i < (w+1); i++)
        for (int j = 0; j < (t+1); j++)
            trans[(i * (t+1)) + j] = 0;

    /* Populate frequence matrix of characters column by column for each word in words*/
    for (int j = 0; j < w; j++)
        for (int i = 0; i < wordsSize; i++)
            f[((words[i][j] - 'a') * w) + j]++;

    /* Base case => trans[0][0]. "1" way to select nothing */
    trans[0] = 1;

    for (int i = 0; i <= t; i++)
    {
        int r = (i * (w + 1)); // Getting the 2 D row index for a 1 D array. 

        for (int j = 0; j < w; j++)
        {
            if (i < t)
            {
                unsigned long long int freq  =   f[ ( ( target[i] - 'a' ) * w ) + j ];
                unsigned long long int dp =  trans[r + j];

                /* res = dp * freq */
                unsigned long long int res =  ((dp % 1000000007) * (freq % 1000000007)) % 1000000007;

                /* trans[i + 1][j + 1] += (trans[i][j] * freq of target[i] char in jth column ) */
                trans[ (r + (w+1) ) + (j + 1)] += res ;
            }

            /* trans[i][j + 1] += trans[i][j] */
            trans[r + (j + 1)] += trans[r + j];
        }
    }

    return trans[(t * (w+1)) + w] % 1000000007;
}

