class Solution {
    int MODULAR = (int) Math.pow(10, 9) + 7;

    // target length - str length + str index <= target index <= str index
    public int numWays(String[] words, String target) {
        int[] countWord = new int[26];
        int[] numWays = new int[target.length()];       

        for(int i = words[0].length() - 1; i >= 0; i --) {
            for(int w = 0; w < words.length; w ++) countWord[words[w].charAt(i) - 'a']++;
            for(int j = Math.max(target.length() - words[0].length() + i, 0); j < target.length() && j <= i; j ++) {
                int t = target.charAt(j) - 'a';
                int multiplier = 1;
                if(j < target.length() - 1) multiplier = numWays[j + 1];
                long temp = (long) countWord[t] * multiplier % MODULAR;
                numWays[j] = (numWays[j] + (int) temp) % MODULAR;
            }

            countWord = new int[26]; 
        }

        return numWays[0];
    }
}
