var mod = 1000000007

func search(charGrid [][]int, idx, k, n, m int, target string, dp [][]int) int {
	if k >= m {
		return 1
	}
	if idx >= n {
		return 0
	}
	if dp[idx][k] != -1 {
		return dp[idx][k]
	}
	var take, notTake = 0, 0
	if charGrid[idx][target[k]-'a'] > 0 {
		take = charGrid[idx][target[k]-'a'] * search(charGrid, idx+1, k+1, n, m, target, dp)
