class Solution {

    String target;
    int[][] cnt;
    int mod = 1000000007;
    long[][] memo;
    public int numWays(String[] words, String target) {
        this.target = target;

        memo = new long[words[0].length()][target.length()];
        cnt = new int[26][words[0].length()];

        for(int i=0; i<words[0].length(); i++){  
            Arrays.fill(memo[i], -1);   
            for(int j=0; j< words.length; j++){
                char c = words[j].charAt(i);
                cnt[c-'a'][i] += 1;
            }
        }

        return  (int)dp(0, 0)%mod;
    }

    public long dp(int j, int i){
        if(j >= target.length()){
            return 1;
        }

        if(i >= cnt[0].length){
            return 0;
        }

        if(memo[i][j] == -1){
            long count = dp(j, i+1)%mod;;
