# Approach : Recursion

# Complexity
- Time complexity: $$O(m*n*2^k)$$

# Code
```
class Solution {
public:
    int mod = 1e9 + 7;

    long long func(vector<vector<int>> &cnt, vector<vector<int>> &dp, string targ, int i, int j) {
        if(j==targ.length())
            return 1;

        if(i==cnt.size())
            return 0;

        if(dp[i][j] != -1) 
            return dp[i][j];

        long long pick = 0, not_pick = 0;
        if(cnt[i][targ[j]-'a']) {
            long long curr = cnt[i][targ[j]-'a'] % mod;
            long long next = func(cnt, dp, targ, i+1, j+1) % mod;
            pick = (curr * next) % mod;
        }

        not_pick = func(cnt, dp, targ, i+1, j) % mod;
        return dp[i][j] = (pick + not_pick) % mod;
    }

    int numWays(vector<string>& words, string target) {
        int m = words[0].length();
        int n = words.size();
        int k = target.length();
        vector<vector<int>> dp(m, vector<int> (k, -1));
        vector<vector<int>> cnt(m, vector<int> (26));
        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                cnt[i][words[j][i]-'a']++;
            }
        }

        return func(cnt, dp, target, 0, 0);
    }
};
```

