class Solution {
public:
    int numWays(vector<string>& words, string target) {
        int m = words[0].length();
        int n = words.size();
        int k = target.length();
        vector<vector<int>> cnt(m, vector<int>(26));
        vector<vector<long long>> dp(m + 1, vector<long long>(k + 1, 0));
        int mod = 1e9 + 7;

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                cnt[i][words[j][i] - 'a']++;
            }
        }

        for (int i = m; i >= 0; i--) {
            for (int j = k; j >= 0; j--) {
                if (j == k) {
                    dp[i][j] = 1;
                } else if (i == m) {
                    dp[i][j] = 0;
                } else {
                    long long not_pick = dp[i + 1][j] % mod;
                    long long pick = 0;
                    if (cnt[i][target[j] - 'a']) {
                        long long curr = cnt[i][target[j] - 'a'] % mod;
                        long long next = dp[i + 1][j + 1] % mod;
                        pick = (curr * next) % mod;
                    }
                    dp[i][j] = (pick + not_pick) % mod;
                }
            }
        }

        return dp[0][0];
    }
};
```

