class Solution {
public:
    int numWays(vector<string>& words, string target) {
        int m = words[0].length();
        int n = words.size();
        int k = target.length();
        vector<vector<int>> cnt(m, vector<int>(26));
        vector<long long> dp(k + 1, 0);
        int mod = 1e9 + 7;

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                cnt[i][words[j][i] - 'a']++;
            }
        }

        dp[k] = 1;
        for (int i = m - 1; i >= 0; i--) {
            for (int j = 0; j <= k; j++) {
                long long not_pick = dp[j] % mod;
                long long pick = 0;
                if (j < k && cnt[i][target[j] - 'a']) {
                    long long curr = cnt[i][target[j] - 'a'] % mod;
                    long long next = dp[j + 1] % mod;
                    pick = (curr * next) % mod;
                }
                dp[j] = (pick + not_pick) % mod;
            }
        }

        return dp[0];
    }
};
