class Solution:
    def numWays(self, words: List[str], target: str) -> int:
        gap = len(words[0]) - len(target) + 1

        words = [Counter(x) for x in zip(*words)]

        dp = [1] * gap + [0]

        for i, c in enumerate(target):
            for k in range(gap):
                dp[k] = dp[k - 1] + dp[k] * words[i + k][c]

        return dp[-2] % (10**9 + 7)
