class Solution {
    public int numWays(String[] words, String target) {
        int n = words.length, m = words[0].length();

        // First, we store the counts of character in each column
        HashMap<Integer, HashMap<Character, Integer>> freq = new HashMap<>();
        for (int i = 0; i < m; i++) {
            freq.put(i + 1, new HashMap<>());
            for (int j = 0; j < n; j++) {
                char c = words[j].charAt(i);
                freq.get(i + 1).merge(c, 1, Integer::sum);
            }
        }

        /**
          create a 2d dp,
          row: i = the i-th char of the target String
          col: j = first j-th chars of all words in the dictionary
        */
        long[][] dp = new long[target.length() + 1][m + 1];
        for (int i = 1; i < dp.length; i++) {
            for (int j = i; j < dp[0].length; j++) {
                char c = target.charAt(i - 1);
                if (freq.get(j).containsKey(c)) {
                    dp[i][j] = dp[i][j - 1] 
                        + freq.get(j).get(c) * (dp[i - 1][j - 1] == 0 && i == 1 ? 1 : dp[i - 1][j - 1]);
                } else dp[i][j] = dp[i][j - 1];
                dp[i][j] %= 1_000_000_007;
            }
        }
        return (int)dp[target.length()][m];
    }
}
