        Arrays.sort(nums);
        int i=0;
        while(start<end)
        {
            ans[i] = nums[start];
            ans[++i] = nums[end];
            start++;
            end--;
            i+=1;
        }
        if(nums.length%2!=0)
            ans[i] = nums[start];
        return ans;
    }
