        bool flag = true;
        int i =0;

        while(!dq.empty()){
            if(flag){
                nums[i] = dq.front();
                dq.pop_front();
                i++;
            }
            else{
                  nums[i] = dq.back();
                  dq.pop_back();
                  i++;
            }

            flag = !flag;
        }

        return nums;

    }
};

