class Solution {
    public int[] rearrangeArray(int[] nums) {

        for(int i = 1; i+1 < nums.length; i++)

            if(nums[i] * 2 == nums[i-1] + nums[i+1]) {

                int a = nums[i+1];

                if(i+2 < nums.length) {
                    nums[i+1] = nums[i+2];
                    nums[i+2] = a;
                }

                else {
                    if(nums[2] == nums[i+1]) {
                        nums[i+1] = nums[1];
