class Solution {
public:
    vector<int> rearrangeArray(vector<int>& nums) {
        for(int i=2;i<nums.size();i++) {
            if((nums[i-2]<nums[i-1] && nums[i-1]<nums[i]) || (nums[i-2]>nums[i-1] && nums[i-1]>nums[i])) {
