class Solution {
    public int[] rearrangeArray(int[] nums) {
        int n = nums.length;
        Arrays.sort(nums);                          //sort the array
        ArrayList<Integer> aux = new ArrayList<>();              
        for(int i = 0; i < n; i++) {
            aux.add(nums[i]);                      // add nums to new aux array
        }
        int j = 0, k = 0, flag = 0;
        for(int i = 0; i < n; i++) {
            if(flag == 0) {
                nums[i] = aux.get(j);            // add back aux to nums array in alternate acending order element.
                j++;
                flag = 1;
            }else {
                nums[i] = aux.get(n - 1- k);    // add back aux to nums array in alternate decending order element.
                k++;
                flag = 0;
            }
        }
        return nums;
    }
}
