class Solution:
    def rearrangeArray(self, nums: List[int]) -> List[int]:
        #Time - O(N) and space - O(1)
        # This is in place replacement
        # Step 1: Even place i has to be less than i-1
        # Step 2: odd place i has to be greater than i-1
        # Step 1 and 2 can be interchanged
        for i in range(len(nums)):
            if i%2:
                if nums[i]>nums[i-1]:
                    nums[i], nums[i-1] = nums[i-1], nums[i]
            else:
                if nums[i]<nums[i-1]:
                    nums[i], nums[i-1] = nums[i-1], nums[i]
        return nums
