class Solution {
public:
    vector<int> rearrangeArray(vector<int>& nums) 
    {
        sort(nums.begin() , nums.end());

        int mid = nums.size()/2;
        queue<int>q1;
        for(int i=0;i<mid;i++)
        {
            q1.push(nums[i]);
        }    
        queue<int>q2;
        for(int i = nums.size()-1 ; i >=0 ; i--)
        {
            q2.push(nums[i]);
        }

        int idx = 0;
        bool flag = true;
        while(idx < nums.size())
        {
            if(flag == true)
            {
                if(q1.size() > 0)
                {
                  nums[idx] = q1.front();
                  q1.pop();
                }
                else if(q2.size() > 0)
                {
                    nums[idx] = q2.front();
                    q2.pop();
                }

            }
            else
            {
                if(q2.size() > 0)
                {
                  nums[idx] = q2.front();
                  q2.pop();
                }
                else if(q1.size() > 0)
                {
                    nums[idx] = q1.front();
                    q1.pop();
                }
            }
            idx++;
            flag = !flag;
        }
        return nums;
    }
};
