class Solution {
public:
    vector<int> rearrangeArray(vector<int>& nums) {
        int i = 1;
        int j = nums.size()- 1;
        while(i<nums.size()-1){
            int a = (nums[i-1]+nums[i+1])/2;
            if(nums[i] == a & j>i){
                if(nums[j]!=a){
                    int temp = nums[j];
                    nums[j]=nums[i];
                    nums[i]=temp;
                    j = nums.size()-1;
                    i = i>1?i-1:i;
                }else{
                    j--;
                }
            }else{
                i++;
            }
        }return nums;

    }
};
