int numComponents(ListNode* h, vector<int>& nums) {
    unordered_set<int> s(begin(nums), end(nums));
    int res = 0, connected = 0;
    for (; h != nullptr; h = h->next) {
        if (s.count(h->val)) {
            res += !connected;
            connected = true;
        }
        else
            connected = false;
    }
    return res;
}
