class Solution {
public:
    int numComponents(ListNode* head, vector<int>& g) {
        if(head==NULL or g.size()<=1) {
            return 1;
        }

        unordered_set<int> out;

        for(int i=0; i<g.size(); i++) {
            out.insert(g[i]);
        }

        ListNode*curr = head;
        int ans = 0;

        while(curr) {
            if(out.count(curr->val)) {
                ans++;

                while(curr->next and out.count(curr->next->val)) {
                    curr = curr->next;
                }                
            } 
            curr = curr->next;
        }
        return ans;
    }
};
