class Solution {
public:
    int numComponents(ListNode* head, vector<int>& nums) {
        ListNode* cur = head;
        int n = 0;
        while (cur) {
            n++;
            cur = cur->next;
        }
        std::vector<int> found (n);
        for (auto& val : nums) {
            found[val] = 1;
        }
        int ans = 0;
        cur = head;
        bool connected = false;
        while (cur) {
            if (connected and found[cur->val] == 0) {
                ans++;
                connected = false;
            }
            else if (!connected and found[cur->val] == 1) {
                connected = true;
            }
            cur = cur->next;
        }
        if (connected) {
            ans++;
        }
        return ans;
    }
};
