       unordered_map<int,bool>mpp;

       for(auto &it : nums) mpp[it] = true;

       int res = nums.size();
       int cnt = 0;

       while(head != NULL)
       {
           if(mpp[head->val] == true) cnt++;

           else
           {
             if(cnt > 1) res -= cnt - 1;
             cnt = 0;   
           }
