class Solution {
    public int numComponents(ListNode head, int[] nums) {
        // finding max element
        int max=0;
        for(int x:nums){
            max= Math.max(max,x);
        }
        // Create an array to check whether the element is present in nums or not or we can use HashSet
        boolean[] isPresent = new boolean[max+1];

        for(int x:nums)
            isPresent[x]=true;

        int count=0, components=0;

        // Traverse the linked list
        while(head!=null)
        {
            // Increase counter if the current value is present in nums array
            if(head.val<=max && isPresent[head.val]){
                count++;
            }
            else{
                // If there are more than one adjacent element in a component, Increase component count and reset counter.
                if(count>0){
                    components++; 
                    count=0;
                }
            }
            head=head.next;
        }
        // Check if there is any remaining component at the end of the linked list
        if(count>0)
            components++;
        return components;
    }
}
