 * };
 */
class Solution {
public:
    int numComponents(ListNode* head, vector<int>& nums) {
        unordered_map<int,int>mp;
        // traversing the nums array and mapping the elements 
        for(auto e:nums){
            mp[e]++;
        }

        int count=0;
        bool flag=true;  //flag variable to keep a check if element belongs to the connected chain or not

        while(head){
