int numComponents(struct ListNode* head, int* nums, int numsSize){
    int n = depth(head);
    bool* exist = calloc(n , sizeof(int));
    for(int i = 0; i < numsSize; i++){
        exist[nums[i]] = true;
    }
    int ans = 0;
    struct ListNode* tmp = head;
    int cn = 0;
    while(tmp){
        if(exist[tmp->val]){
            cn++;
        }
        else{
            if(cn)
                ans++;
            cn = 0;
        }
        tmp = tmp->next;
    }
    if(cn)
        ans++;
    free(exist);
    return ans;
}
