/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public int numComponents(ListNode head, int[] nums) {
        Set<Integer> numSet = new HashSet<>();

        for(int i = 0; i < nums.length; i++) {
            numSet.add(nums[i]);
        }

        int sequences = 0;


        boolean inASequence = false;

        while(head != null) {

            if(inASequence) {
                if(numSet.contains(head.val)){ // If we are in a sequence and we encounter something part of array just continue.
                    head = head.next;
                    continue;
                } 
                inASequence = false;
                head = head.next;
            } else { // If we are not in a sequence and a Sequence starts then increase sequence count and set flag to true.
                if(numSet.contains(head.val)) {
                    inASequence = true;
                    sequences++;
                }
                head = head.next;   
            }
        }
        return sequences;
    }
}

