class Solution {
public:
    int numComponents(ListNode* head, vector<int>& nums) {
        int ans=0;
        auto p = head;
        map<int,int> m;
        set<int> s;
        for(auto x:nums) s.insert(x);
        while(p!=NULL && p->next!=NULL){
            int a = p->val;
            m[a]=p->next->val;
            p=p->next;
        }
        m[p->val]=-1;
        for(auto x:nums){
            if(s.find(m[x])!=s.end()) continue;
            ans++;
        }

        return ans;
    }
};
