        int ans = 0;
        while (head) {
            if (binary_search(nums.begin(), nums.end(), head->val)) {
                head = head->next;
                while (head and binary_search(nums.begin(), nums.end(), head->val))
                    head = head->next;
                ans++;
            }
            else
                head = head->next;
        }
        return ans;
