class Solution {
public:
    int numComponents(ListNode* head, vector<int>& nums) {
      if(!head) return 0;

      int n = nums.size();

      if(n == 0) return 0;

      map<int,bool> vals;

      for(int i = 0; i < n; ++i) {
        vals[nums[i]] = true;
      }

      int noOfComponents = 0;

      for(ListNode* ptr = head; ptr != nullptr; ptr = ptr->next) {
        if(vals.find(ptr->val) != vals.end()) {
          while(ptr && vals.find(ptr->val) != vals.end()) {
            ptr = ptr->next;
          }
          ++noOfComponents;
        }
        if(!ptr) break;
      }

      return noOfComponents;
    }
};
