/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    int numComponents(ListNode* head, vector<int>& G) {
        if (G.size() == 0) {
            return 0;
        }
        sort(G.begin(), G.end()); // O(G * log(G))
        int count = 0;
        bool connecting = false;

        // O(linked_list_length)
        while (head != nullptr) {

            // O(log(G))
            if (binary_search(G.begin(), G.end(), head->val)) {
                if (!connecting) {
                    connecting = true;
                    count++;
                }
            } else if (connecting) {
                connecting = false;
            }
            head = head->next;
        }

        return count;
    }
};
