class Solution {
    public int numComponents(ListNode head, int[] G) {
        boolean[] map = new boolean[10001];
        for(int num : G) {
            map[num] = true;
        }

        if(head == null) {
            return 0;
        }

        ListNode runner = head;

        // slide runner to the next possible component
        while(runner != null && !map[runner.val]) {
            runner = runner.next;
        }

        // no connected components
        if(runner == null) {
            return 0;
        }

        int connected = 1;
        while(runner != null) {
            if(!map[runner.val]) {
                // slide runner to the next possible component
                while(runner != null && !map[runner.val]) {
                    runner = runner.next;
                }

                // increase component count if we did not reach to the end
                // of the linked list
                if(runner != null) {                    
                    connected++;
                }
            } else {
                runner = runner.next;
            }
        }

        return connected;
    }
}
