class Solution {
public:
    bool binarysearch(vector<int> &G,int k){
        int si = 0;
        int ei = G.size()-1;
        while(si<=ei){
            int mid = (si+ei)/2;
            if(G[mid]==k) return true;
            else if(G[mid]<k) si=mid+1;
            else ei = mid-1;
        }
        return false;
    }
    int numComponents(ListNode* head, vector<int>& G) {
        sort(G.begin(),G.end());
        ListNode *temp = head;
        int components = 0;
        int currsize = 0;
        while(temp){
            int val = temp->val;
            bool b = binarysearch(G,val);
            if(b){
               currsize++; 
            }else{
                if(currsize) components++;
                currsize = 0;
            }
            temp = temp->next;
        }
        if(currsize) components++;
        return components;
    }
};
