public class Solution {
    public int NumComponents(ListNode head, int[] G) {
        HashSet<int> hash = new HashSet<int>();
        foreach(int x in G)
            hash.Add(x);

        while(head != null)
        {
            if(head.next != null && hash.Contains(head.next.val))
                hash.Remove(head.val);
            head = head.next;
        }

        return hash.Count;
    }
}
