const numComponents = (head, G) => {
    const set = new Set(G);
    let pointer = head,
		stack = [],
		ans = 0;

    while (pointer) {
		// If there's overlapped number, push to stack  
        if (set.has(pointer.val)) {
            stack.push(pointer.val);
        } else {
			// Once the continuous stopped and there's something in the stack, ans++  
            if (stack.length) {
                ans++;
                stack = [];
            }
        }
        pointer = pointer.next;
    }

	// Check if there's numbers left in stack
    return (stack.length) ? ans + 1 : ans;
};
