// Program to find the number of connected components in "nums" where two values are connected if they appear consecutively in the linked list ~ coded by Hiren
#include <iostream>
#include <vector>
#include <unordered_set>

// List template
struct Node {
    int val;
    Node* next;

    // Init constructor
    Node(int val, Node* next = nullptr)
    :
    val(val), next(next) {}

    // Init destructor
    ~Node() {
        if(next) {
            delete next;
        }
    }
};

// Method to find the number of connected components - O(N) & O(N) : Where N is the total number of nodes of the list
int numComponents(Node* head, std::vector<int> nums) {
    std::unordered_set<int> set(begin(nums), end(nums));

    // Require to store the number of connected components (resultant value)
    int count = 0;

    while(head) {
        // Increment the count if its the beginning of a new component
        if(set.count(head->val)) {
            count++;
            // Skip the consecutive nodes of the current component
            while(head && set.count(head->val)) {
                head = head->next;
            }
        }
        // Else, skip the non-component nodes until a potential component node is found
        else {
            while(head && !set.count(head->val)) {
                head = head->next;
            }
        }
    }

    return count;
}
// Note: There are also multiple solutions of this problem, do check it out.

// Method to print the list using recursion
void printList(Node* head) {
    if(head) {
        std::cout<<head->val<<' ';
        printList(head->next);
    }
}

// Driver code
int main() {
    // Creating, connecting nodes and initializing their data
    Node* head = new Node(0, new Node(1, new Node(2, new Node(3))));

    // Print call
    printList(head);

    // Call to find the number of connected components
    int count = numComponents(head, {0,1,3});
    std::cout<<"\nThe number of connected components is : "<<count;

    // Deletion call
    delete head;

    return 0;
}
// Link: https://leetcode.com/problems/linked-list-components/description/
