        int n=nums.size();
        for(int i=0;i<n;i++) uSet.insert(nums[i]);
        bool flag=false;
        int count=0;
        while(temp){
            if(!flag && uSet.find(temp->val)!=uSet.end()){
                flag=true;
                count++;
            }
            else if(flag && uSet.find(temp->val)==uSet.end())
                flag=false;
            temp=temp->next;
        }
        return count;
    }
};
