/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* removeZeroSumSublists(ListNode* head) {
        if(head == NULL) return 0;
        unordered_map<int, ListNode*> m;
        ListNode* dummy = new ListNode(0);
        dummy->next = head;
        m[0] = dummy;
        int prefix_sum = 0;
        while(head != NULL)
        {
            prefix_sum = prefix_sum + head->val; // prefix_sum += head->val;
            if(m.find(prefix_sum) != m.end()){
                    ListNode* start = m[prefix_sum];
                    int sum = prefix_sum;
                    while(start != NULL && start != head)
                    {
                        start = start->next;
                        sum+=start->val;
                        if(start != head)
                        {
                            m.erase(sum);
                        }

                    } 
                    m[prefix_sum]->next = head->next;     
            }
            else{
                m[prefix_sum] = head; // jaha pr jo sum mila us ke head node ko map se maapp krdenge 
            }
            head = head->next;
        }
        return dummy->next;
    }
};
```

