/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public ListNode removeZeroSumSublists(ListNode head) {
        if(head == null) return null;
        HashMap < Integer , ListNode> hm = new HashMap<>();
        //we make a hashmap so that further we can manipulate sum and nodes.
        ListNode dummy = new ListNode(0), curr = head;
        hm.put(0 , dummy); //initiating dummy node with 0 not -1 because -1 will hinder the sum value and 0 do not
        dummy.next = head;
        int sum = 0;

        while(curr != null ){
            sum += curr.val;

            // if hashmap contains the sum then it means that the nodes in between the last same sum value and current values may have cancelled each other thus the sum is same again and we need to remove those nodes.
            if(hm.containsKey(sum)){
                int currSum = sum;
                ListNode toRemove = hm.get(sum).next;
                while(toRemove != curr){

                    currSum += toRemove.val;
                    hm.remove(currSum);
                    toRemove = toRemove.next;
                }
                hm.get(sum).next = curr.next;
            }else{ 
                // if the hashmap don't contains the same sum value that means the nodes.val do not cancel each other
                hm.put(sum , curr);
            }
            curr = curr.next;
        }
        return dummy.next;
    }
}


