```
class Solution {
public:
    ListNode* removeZeroSumSublists(ListNode* head) {
        ListNode *ptr = new ListNode();
        ptr -> next = head;
        for (ListNode *i = ptr; i != NULL; i = i -> next) {
            int sum = 0;
            for (ListNode *j = i -> next; j != NULL; j = j -> next) {
                sum += j -> val;
                if (sum == 0) {
                    if(j -> next != NULL)
                        i -> next = j -> next;
                    else {
                        i -> next = NULL; 
                        break; 
                    }
                }
            }
        }        
        return ptr -> next;
    }
};
```


