class Solution {
public:
    ListNode* removeZeroSumSublists(ListNode* head) {
        ListNode *ptr = new ListNode(0);
        ptr -> next = head;
        unordered_map<int, ListNode*> map;
        map[0] = ptr;
        int totalSum = 0;
        while (head != NULL) {
            totalSum += head -> val;
            if (map.find(totalSum) != map.end()) {
                ListNode *pre = map[totalSum];
                ListNode *cur = pre;
                int temp = totalSum;
                while (pre != head) {
                    pre = pre -> next;
                    temp += pre -> val;
                    if (pre != head)
                        map.erase(temp);
                }
                cur -> next = head -> next;
            }
            else 
                map[totalSum] = head;
            head = head -> next;
        }
        return ptr -> next;
    }
};
