class Solution:
    def removeZeroSumSublists(self, head: ListNode) -> ListNode:
        s = 0
        cur = head

        dummy = ListNode(0)
        dummy.next = head
        d = {0: dummy}

        while cur:
            s += cur.val
            if s not in d:
                d[s] = cur
            else:
                d[s].next = cur.next
            cur = cur.next
        return dummy.next
