       vector<int>nums;
       for(auto node = head; node; node = node->next){
           int value = node->val;
           nums.push_back(value);
       }
       int n = nums.size();

       vector<vector<int>>values(n,vector<int>(3,-1));
       for(int i=0;i<n;i++){
           int sum = 0;
           for(int j=i;j<n;j++){
