class Solution {
public:
    ListNode* removeZeroSumSublists(ListNode* head) {

        ListNode dummy(0);
        dummy.next = head;

        bool sumZeroFound = true;

        while(sumZeroFound){ // we need to check is new list still have zero sum
            sumZeroFound = false; // reset 

            ListNode* iter = &dummy;
            unordered_map<int, ListNode*> prefixSumTable;
            prefixSumTable.insert({0, iter});
            iter = iter->next; // start from head node
            int currSum = 0;

            while(iter){
                currSum += iter->val;
                if(prefixSumTable.find(currSum) != prefixSumTable.end()){
                    ListNode* start = prefixSumTable[currSum];
                    start->next = iter->next;
                    sumZeroFound = true; /// there will be changes in list
                }else{
                    prefixSumTable.insert({currSum, iter});
                }
                iter = iter->next;
            }
        }

        return dummy.next;
    }
};

