var removeZeroSumSublists = function(head) {
    const prefixSum = [];
    const elements = []

    let headCopy = head;

    while(headCopy){
        prefixSum[prefixSum.length] = (prefixSum.length-1) >= 0 ? 
            prefixSum[prefixSum.length -1] + headCopy.val 
        : headCopy.val;

        elements[elements.length] = headCopy.val;

        headCopy = headCopy.next;
    }


    let map = new Map();
    map.set(0, -1);

    for(let i=0; i<prefixSum.length; i++){
        if(!elements[i]){
            elements[i] = '-';
        } else {
            if(map.has(prefixSum[i])){
               const prevIndex = map.get(prefixSum[i]);
                for(let j=i; j> prevIndex; j--){
                    if(j!=i && prefixSum[j]!=0 && elements[j] != '-') map.delete(prefixSum[j]);
                    elements[j] = '-';
                }
            }else{
                map.set(prefixSum[i], i);   
            }
        }   
    }

    return elements.reduce((acc, val) => {
        if(val != '-'){
            if(acc[0] === null){
                const tmp = [];
                tmp[0] = new ListNode(val);
                tmp[1] = tmp[0];
                return tmp;
            } else {
                acc[1].next = new ListNode(val);
                acc[1] = acc[1].next;
            }
        }
        return acc;
    }, [null])[0];
};

