class Solution {
public:

  ListNode* removeZeroSumSublists(ListNode* head) {
    ListNode *fh = new ListNode(0, head);
    unordered_map<int, ListNode*> mp;
    mp[0] = fh;
    int sum = 0;

    while( head ){
      sum += head->val;

      if(mp.count(sum)){
        ListNode *tmp = mp[sum]->next;
        for(;tmp != head; tmp = tmp->next){
          sum += tmp->val;
          mp.erase(sum);
        }
        sum += head->val;
        mp[sum]->next = head->next;
      } 
      else mp[sum] = head; 
      head = head->next;
    }

    return fh->next;
  }
};
