ListNode* removeZeroSumSublists(ListNode* head) {

        unordered_map<int, ListNode*> jump;

        int sum {0};
        ListNode *origine = new ListNode(0, head);

        for (auto cur = origine; cur != nullptr; cur = cur->next) {
