class Solution:
    def removeZeroSumSublists(self, head: Optional[ListNode]) -> Optional[ListNode]:
        if not head:return head
        head = ListNode(0, head)
        sum_dict, stack = {0:head}, [0]
        cur,total = head.next, 0
        while cur:
            total += cur.val
            if total not in sum_dict:
                sum_dict[total] = cur
                stack.append(total)
            else:
                sum_dict[total].next = cur.next
                while stack[-1]!=total:
                    val = stack.pop()
                    del sum_dict[val]
            cur = cur.next
        return head.next
