class Solution {
public:
    ListNode* removeZeroSumSublists(ListNode* head) {
        int n = 0;
        vector<int> ans;
        ListNode* temp = head;
        while(temp){
            ans.push_back(temp->val);
            temp = temp->next;
            n++;
        }
        vector<int> isDeleted(n,0);
        int index = 0;
        while(index < n){
            if(!isDeleted[index]){
                int sum = ans[index];
                int j = index + 1;
                while(j<n){
                    if(sum == 0) break;
                    if(!isDeleted[j]) sum = sum + ans[j];
                    j++;
                }
                if(sum == 0){
                    for(int k=index; k<j; k++) isDeleted[k] = 1;
                }
            }
            index++;
        }
        temp = head;
        ListNode* root = NULL;
        ListNode* prev = NULL;
        for(int i=0; i<n; i++){
            if(!isDeleted[i]){
                temp->val = ans[i];
                if(!root) root = temp;
                prev = temp;
                temp = temp->next;
            }
        }
        if(prev) prev->next = NULL;
        return root;
    }
};
