class Solution {
public:
    ListNode* removeZeroSumSublists(ListNode* head) {

        struct ListNode *t, *p, *q; //p is the main pointer, t is a following pointer on
        p = t = q = head;           //p and q is the forward moving pointer to check sum
        int change = 0, sum = 0;

        if(head->val == 0 && head->next == NULL)
            return NULL;

        while(p || change>0)
        {
            if(change>0)
            {
                change--;
                p = head; //restart checking as a change has been made
            }
            q = p;
            sum = 0;
            while(q)
            {
                sum = sum + q->val;
                if(sum == 0)
                {
                    change++;
                    if(p==head)
                        head = q->next;
                    else
                        t->next = q->next;
                }
                q = q->next;
            }

            t = p; //following pointer
            if(p != NULL)
                p = p->next;    
        }

        return head;
    }
};
